mapper.select:
select r.id,u.real_name,r.title,r.create_time from t_repo r join t_user u on r.user_id = u.id


        r.title like concat('%',#{title},'%')


        r.user_id = #{userId}

select r.id,r.user_id,r.title, sum(case when q.qu_type = 1 then 1 else 0 end) as radioNum, sum(case when q.qu_type = 2 then 1 else 0 end) as multiNum, sum(case when q.qu_type = 3 then 1 else 0 end) as judgeNum,sum(case when q.qu_type = 4 then 1 else 0 end) as saqNum from t_repo as r join t_question as q on r.id = q.repo_id group by r.id,r.title
r.title like concat('%',#{repoTitle},'%')
r.user_id = #{userId}
